import React , { useState } from 'react';
import './Cards.css';
import CardItem from './CardItem';
import Modal from './pages/Modal';
// import Modal from './Modal';

function Cards() {
  const [selectedService, setSelectedService] = useState(null);

  const handleServiceClick = (service) => {
    setSelectedService(service);
  };

  const handleCloseModal = () => {
    setSelectedService(null);
  };

  return (
    <div className='cards'>
      <h1>Check out all Services!</h1>
      <div className='cards__container'>
        <div className='cards__wrapper'>
          <ul className='cards__items'>
            <CardItem
              src='images/towing.webp'
              text='  Vehicle Towing            '
              label='Vechile Towing Service'
              description= 'We offer 24/7 towing service for vehicles of all sizes.'
              path='/mapGarage'

              onClick={() =>
                handleServiceClick({
                  title: 'Vehicle Towing Service',
                  description: 'We offer 24/7 towing service for vehicles of all sizes.',
                  buttonText: 'Login to book a tow',
                  buttonPath: '/signup',
                })
              }
            />
            {/* <CardItem
              src='images/medical.png'
              text='On-Spot Medical Treatment Service            '
              label='Medical Treatment'
              path='/map'
              onClick={() =>
                handleServiceClick({
                  title: 'On-Spot Medical Treatment Service',
                  description: 'Our medical team is available 24/7 to provide on-spot treatment to those in need.',
                  buttonText: 'Login to request medical help',
                  buttonPath: '/signup',
                })
              }
            /> */}
          </ul>
          <ul className='cards__items'>
            <CardItem
              src='images/petrol_dilevery.jpg'
              text='Fuel Delivery Service        '
              label='Fuel Dilevery'
              path='/mapPetrolPump'
              onClick={() =>
                handleServiceClick({
                  title: 'Fuel Delivery Service',
                  description: 'We deliver fuel to you when you need it, wherever you are.',
                  buttonText: 'Login to request fuel delivery',
                  buttonPath: '/signup',
                })
              }
            />
            <CardItem
              src='images/tire puncture.jpg'
              text='Tire Puncture Repair Service            '
              label='Tire puncture Repair'
              path='/mapGarage'
              onClick={() =>
                handleServiceClick({
                  title: 'Tire Puncture Repair Service',
                  description: 'We provide on-spot tire puncture repair for all types of vehicles.',
                  buttonText: 'Login to request tire repair',
                  buttonPath: '/signup',
                })
              }
            />
            <CardItem
              src='images/battery.jpg'
              text='Battery Service                           '
              label='Battery'
              path='/mapGarage'
              onClick={() =>
                handleServiceClick({
                  title: 'Battery Service',
                  description: 'We offer battery replacement and jump-start services for all types of vehicles.',
                  buttonText: 'Login to request battery service',
                  buttonPath: '/signup',
                })
              }
            />
          </ul>
        </div>
      </div>
      {selectedService && (
        <Modal title={selectedService.title} onClose={handleCloseModal}>
          <p>{selectedService.description}</p>
          <a href={selectedService.buttonPath}>{selectedService.buttonText}</a>
        </Modal>
      )}
    </div>
  );
}

export default Cards;
