import React, { useState, useEffect } from 'react';

export default function AdminReq(props) {
    const { requestId } = props;
    const [selectedRequestId, setSelectedRequestId] = useState(null);
    const [modalOpen, setModalOpen] = useState(false);

  const [garageRequests, setGarageRequests] = useState([]);

  useEffect(() => {
    fetchGarageRequests();
  }, []);

  async function fetchGarageRequests() {
    try {
      const response = await fetch('/api/requests/garage');
      const data = await response.json();

      if (data.success) {
        // const garageRequests = data.garageRequests.filter((request) => request.status === 'pending');
        setGarageRequests(data.garageRequests);
      }
    } catch (error) {
      console.error(error);
    }
  }

  async function updateGarageRequestStatus(requestId, status) {
    // Implement this function to update the status of the request with the given ID
  }

  function handleActionClick(requestId) {
    showModal(requestId);
  }

  async function handleAcceptClick(requestId) {
    try {
      const response = await fetch(`/api/requests/garage/${requestId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status: 'accepted' }),
      });
      const data = await response.json();

      if (data.success) {
        setModalMessage('Request accepted successfully');
        setModalVisible(true);
        setSelectedRequest(data.updatedGarageRequest);
        fetchGarageRequests();
      }
    } catch (error) {
      console.error(error);
    }
  }

  async function handleDeclineClick(requestId) {
    try {
      const response = await fetch(`/api/requests/garage/${requestId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status: 'declined' }),
      });
      const data = await response.json();

      if (data.success) {
        setModalMessage('Request declined successfully');
        setModalVisible(true);
        setSelectedRequest(data.updatedGarageRequest);
        fetchGarageRequests();
      }
    } catch (error) {
      console.error(error);
    }
  }


  async function updateGarageRequestStatus(requestId, status) {
    try {
      await fetch(`/api/requests/garage/${requestId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status }),
      });
    } catch (error) {
      console.error(error);
    }
  }

  function showModal(requestId) {
    setSelectedRequestId(requestId);
    setModalOpen(true);
  }

  function closeModal() {
    setSelectedRequestId(null);
    setModalOpen(false);
  }

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Email</th>
            <th>Service Type</th>
            <th>Location</th>
            <th>Vehicle Number</th>
            <th>Vehicle Make</th>
            <th>Vehicle Type</th>
            <th>Additional Details</th>
            <th>Assigned Admin</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
        {garageRequests.map((request) => (
            <tr key={request._id}>
              <td>{request.customer_details.email}</td>
              <td>{request.service_type}</td>
              <td>{request.location}</td>
              <td>{request.vehicle_number}</td>
              <td>{request.vehicle_make}</td>
              <td>{request.vehicle_type}</td>
              <td>{request.additional_details}</td>
              <td>{request.assigned_admin}</td>
              <td>{request.status}</td>
              <td>
                <button
                  className="action-button"
                  onClick={() => showModal(request._id)}
                >
                  Action
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal */}
      {modalOpen &&
      <div id="modal-container">
        <div id="modal-content">
          <p>Are you sure you want to accept this request?</p>
          <div>
            <button id="accept-button" onClick={() => handleAcceptClick(requestId)}>
                ACCEPT</button>
                <button id="decline-button" onClick={() => handleDeclineClick(requestId)}>
                DECLINE</button>
                </div>
                </div>
                </div>}
                </div>
                );}
