import React from 'react';
// import '../../App.css';
import './order.css';

export default function Request(props) {
  const searchParams = new URLSearchParams(props.location.search);
  this.label = searchParams.get("label");
  this.st = searchParams.get("st");
  this.id = searchParams.get("id");
  this.vn = searchParams.get("vn");
  this.vm = searchParams.get("vm");
  this.ad = searchParams.get("ad");

  

  // const {id} = id
      return (
        <div className="order-confirmation-container">
          <h2 >Order Details</h2>
          <div className="order-details">
            <div className="order-item">
              <span>Service Type: </span>
              <span style={{
          color: 'brown',
        }}>Vehicle Towing</span>
            </div>
            <div className="order-item">
              <span>Location:</span>
              {/* <span>{this.props.match.params.order_id}</span> */}
              <span style={{
          color: 'brown',
        }}>{this.st}</span>
            </div>


            


            <div className="order-item">
              <span>Vehicle Type:</span>

              <Select
            value={institution}
            onChange={(e) => setInstitution(e.target.value)}
          >
            <option value="" disabled selected hidden>
              Choose an Institution
            </option>
            <option value="Hospital">Hospital</option>
            <option value="Garage">Garage</option>
            <option value="PetrolPump">Petrol Pump</option>
          </Select>
          
              <span style={{
          color: 'brown',
        }}>{this.vm}</span>
        </div>


            <div className="order-item">
              <span>Vehicle Make:</span>
              <span style={{
          color: 'brown',
        }}>{this.vm}</span>
            </div>
            <div className="order-item">
              <span >Vehicle Number:</span>
              <span style={{
          color: 'brown',
        }}>{this.vn}</span>
            </div>
            <div className="order-item">
              <span>Additional Details:</span>
              <span style={{
          color: 'brown',
        }}>{this.ad}</span>
            </div>
          </div>
          <p className="confirmation-message">
            Thank you for your order! Our team will be in touch with you shortly.
          </p>
        </div>
    //     <div>
            
        // </div>
    );}
