import React, { Component } from 'react';
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';

class MapContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      lat: null,
      lng: null,
      locations: [
        {
          name: 'Malot Hospital',
          lat: 26.8436,
          lng: 75.6551,
        },
        {
          name: 'Garhwal Family',
          lat: 26.8505,
          lng: 75.6508,
        }
      ],
    };
  }

  componentDidMount() {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        this.setState({
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        });
      },
      () => {
        console.log('Error getting current position');
      }
    );
  }

  render() {
    const { lat, lng, locations } = this.state;

    if (!lat || !lng) {
      return <div>Loading...</div>;
    }

    const mapStyles = {
      width: '100%',
      height: '500px',
    };

    const mapOptions = {
      center: { lat, lng },
      zoom: 16,
    };
    const myLocationIcon = {
      url: 'https://developers.google.com/maps/documentation/javascript/examples/full/images/beachflag.png',
    };
  
    const otherLocationIcon = {
      url: 'https://developers.google.com/maps/documentation/javascript/examples/full/images/parking_lot_maps.png',
    };

    return (
      <Map
        google={this.props.google}
        zoom={mapOptions.zoom}
        style={mapStyles}
        initialCenter={mapOptions.center}
      >
        {locations.map((location) => (
          <Marker
            key={location.name}
            name={location.name}
            position={{ lat: location.lat, lng: location.lng }}
            icon={location.name === 'Malot Hospital' ? otherLocationIcon : myLocationIcon}
            // icon={
            //   location.name === 'Malot Hospital'
            //     ? 'https://developers.google.com/maps/documentation/javascript/examples/full/images/beachflag.png'
            //     : ''
            // }
          />
        ))}
        <Marker
          name={'Garhwal Family'}
          position={{ lat, lng }}
          icon={myLocationIcon}
          // icon={{
          //   url: 'https://developers.google.com/maps/documentation/javascript/examples/full/images/beachflag.png',
          // }}
        />
      </Map>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: 'AIzaSyC_9cAGZnlvSGLKRUMCxIgteTpaMvE83oY',
})(MapContainer);
