import React from 'react';
// import '../../App.css';
import { useEffect , useState} from "react";
import './order.css';

export default function Request(props) {
  const [label, setLabel] = useState("");
  const [vm, setVm] = useState("");
  const [vn, setVn] = useState("");
  const [ad, setAd] = useState("");


  useEffect(() => {
    const searchParams = new URLSearchParams(window.location.search);
    setLabel(searchParams.get("label"));
    setVm(searchParams.get("vm"));
    setVn(searchParams.get("vn"));
    setAd(searchParams.get("ad"));
  }, []);

  // const searchParams = new URLSearchParams(props.location.search);
  // const label = searchParams.get("label");
  // // const st = searchParams.get("st");
  // const id = searchParams.get("id");
  // const vn = searchParams.get("vn");
  // const vm = searchParams.get("vm");
  // const ad = searchParams.get("ad");

  

  // const {id} = id
      return (
        <div className="order-confirmation-container">
          <h2 >Order Details</h2>
          <div className="order-details">
            <div className="order-item">
              <span>Service Type: </span>
              <span style={{
          color: 'brown',
        }}>{label}</span>
            </div>
            <div className="order-item">
              <span>Location:</span>
              {/* <span>{this.props.match.params.order_id}</span> */}
              <span style={{
          color: 'brown',
        }}>{label}</span>
            </div>
            <div className="order-item">
              <span>Vehicle Type:</span>
              <span style={{
          color: 'brown',
        }}>Four Wheeler</span>
            </div>
            <div className="order-item">
              <span>Vehicle Make:</span>
              <span style={{
          color: 'brown',
        }}>{vm}</span>
            </div>
            <div className="order-item">
              <span >Vehicle Number:</span>
              <span style={{
          color: 'brown',
        }}>{vn}</span>
            </div>
            <div className="order-item">
              <span>Additional Details:</span>
              <span style={{
          color: 'brown',
        }}>{ad}</span>
            </div>
          </div>
          <p className="confirmation-message">
            Thank you for your order! Our team will be in touch with you shortly.
          </p>
        </div>
    //     <div>
            
        // </div>
    );}
