import React , { useState } from 'react';
import './Cards.css';
import CardItem from './CardItem';
import Modal from './pages/Modal';
// import Modal from './Modal';

function Cards() {
  // const [selectedService, setSelectedService] = useState(null);

  const [selectedCard, setSelectedCard] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false); // new state to track login status




  const handleCardClick = (index) => {
    setSelectedCard(index);
  };

  const handleCloseModal = () => {
    setSelectedCard(null);
  };

  const handleLogin = () => {
    setIsLoggedIn(true);
    window.location.href = "/sign-up";
  };

  return (
    <div className='cards'>
      <h1>Check out all Services!</h1>
      <div className='cards__container'>
        <div className='cards__wrapper'>
          <ul className='cards__items'>

          {cards.slice(0, 2).map((card, index) => (
              <CardItem
                key={index}
                src={card.src}
                text={card.text}
                label={card.label}
                path={card.path}
                description={card.description}
                buttonText={card.buttonText}
                onClick={() => handleCardClick(index)}
              />
            ))}
          </ul>
          <ul className='cards__items'>
            {cards.slice(2, 5).map((card, index) => (
              <CardItem
                key={index}
                src={card.src}
                text={card.text}
                label={card.label}
                path={card.path}
                description={card.description}
                buttonText={card.buttonText}
                onClick={() => handleCardClick(index + 2)}
              />
            ))}


          </ul>
        </div>
      </div>
      {selectedCard !== null && (
        <Modal
          title={cards[selectedCard].label}
          description={cards[selectedCard].text}
          buttonText={cards[selectedCard].buttonText}
          handleLogin={handleLogin}
        >
          <button onClick={handleLogin}>Login</button>
        </Modal>
      )}
      {isLoggedIn && selectedCard !== null ? window.location.href = cards[selectedCard].path : null}


    </div>
  );
}

export default Cards;
