    const mapOptions = {
      center: { lat, lng },
      zoom: 16,
    };
  
    const searchStyles = {
      width: '50%',
      height: '100%',
      padding: '10px',
      boxSizing: 'border-box',
      margin: '0 0 0 20px', // added margin
